import React, { Component, useState } from "react";
import Navbar from "../components/NavBar";
import { GoogleLogin } from "react-google-login";
import Logout from "../Logout";
import { CLIENT_ID } from "../constants/credentials";

const Login = () => {
  //save data in local storage so that only one time login is required for id.

  const [logindata,setLoginData] = useState(
    localStorage.getItem('loginData')
    ?JSON.parse(localStorage.getItem('loginData'))
    :null
  );
  const onSuccess = (res) => {
    console.log("Login Successful! Current User is :", res.profileObj);
  };
  const onFailure = (res) => {
    console.log("Login Failed! Res : ", res);
  };
  const handleLogin = async(googleData) =>{
const res = await fetch('/api/google-login',{
  method:'POST',
  body:JSON.stringify({
    token:googleData.tokenId,
  }),
  headers:{
'Content-Type': 'application/json',
  },
});

const data = await res.json();
setLoginData(data);
localStorage.setItem('loginData', JSON.stringify(data));
  }
  return (
    <div style={styles.container}>
   
      <div style={styles.parentDiv}>
        <div style={styles.childDiv1}>
          <div>Login</div>

          {/* change the isSignedIn to a dynamic one instead of always true */}
          <GoogleLogin
            clientId={CLIENT_ID}
            buttonText="Login"
            // onSuccess={onSuccess}
            onFailure={onFailure}
            cookiePolicy={"single_host_origin"}
            handleLogin= {handleLogin}
            // isSignedIn = {true}
          />
        </div>
        <div style={styles.childDiv2}>Hello</div>
        <div style={styles.childDiv3}>
          {" "}
          
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    marginTop: 60,
    flexDirection: "column",
  },
  parentDiv: {
    display: "flex",
  },
  childDiv1: {
    flex: 1,

    margin: 10,
  },
  childDiv2: {
    flex: 3,

    margin: 10,
  },
  childDiv3: {
    flex: 1,

    margin: 10,
  },
};

export default Login;
